import React, { useState, useEffect } from 'react';
import './App.css';

// Funci√≥n para hashear contrase√±as (simulaci√≥n simple)
const hashPassword = (password) => {
  let hash = 0;
  for (let i = 0; i < password.length; i++) {
    const char = password.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash;
  }
  return hash.toString();
};

// Categor√≠as predefinidas
const CATEGORIES = [
  { id: 'personal', name: 'Personal', icon: 'üë§', color: '#667eea' },
  { id: 'supermercado', name: 'Supermercado', icon: 'üõí', color: '#4ecdc4' },
  { id: 'salud', name: 'Salud', icon: 'üè•', color: '#ff6b6b' },
  { id: 'citas', name: 'Citas', icon: 'üìÖ', color: '#feca57' },
  { id: 'reuniones', name: 'Reuniones', icon: 'üë•', color: '#48dbfb' },
  { id: 'llamadas', name: 'Llamadas', icon: 'üìû', color: '#ff9ff3' },
  { id: 'trabajo', name: 'Trabajo', icon: 'üíº', color: '#54a0ff' },
  { id: 'estudios', name: 'Estudios', icon: 'üìö', color: '#5f27cd' },
  { id: 'hogar', name: 'Hogar', icon: 'üè†', color: '#00d2d3' },
  { id: 'otros', name: 'Otros', icon: 'üìù', color: '#6c757d' }
];

// Componente de Login/Registro con dise√±o profesional
const AuthForm = ({ onLogin }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [fullName, setFullName] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Funci√≥n para obtener usuarios del localStorage
  const getUsersFromStorage = () => {
    try {
      const storedUsers = localStorage.getItem('todoapp_users');
      return storedUsers ? JSON.parse(storedUsers) : [];
    } catch (error) {
      console.error('Error al leer usuarios del localStorage:', error);
      return [];
    }
  };

  // Funci√≥n para guardar usuarios en localStorage
  const saveUsersToStorage = (users) => {
    try {
      localStorage.setItem('todoapp_users', JSON.stringify(users));
    } catch (error) {
      console.error('Error al guardar usuarios en localStorage:', error);
    }
  };

  // Inicializar usuarios desde localStorage
  const [users, setUsers] = useState(() => {
    const storedUsers = getUsersFromStorage();
    // Si no hay usuarios, crear uno de prueba
    if (storedUsers.length === 0) {
      const defaultUsers = [
        { 
          email: 'prueba@prueba.com', 
          password: hashPassword('123456'),
          fullName: 'Usuario de Prueba'
        }
      ];
      saveUsersToStorage(defaultUsers);
      return defaultUsers;
    }
    return storedUsers;
  });

  // Actualizar localStorage cuando cambie el estado de usuarios
  useEffect(() => {
    saveUsersToStorage(users);
  }, [users]);

  // Validaciones en tiempo real
  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const getPasswordStrength = (password) => {
    if (password.length < 6) return { strength: 'weak', text: 'Muy d√©bil', color: '#ff4757' };
    if (password.length < 8) return { strength: 'medium', text: 'Media', color: '#ffa502' };
    if (password.length >= 8 && /(?=.*[a-z])(?=.*[A-Z])(?=.*\d)/.test(password)) {
      return { strength: 'strong', text: 'Fuerte', color: '#2ed573' };
    }
    return { strength: 'medium', text: 'Media', color: '#ffa502' };
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setIsLoading(true);

    // Simular delay de red
    await new Promise(resolve => setTimeout(resolve, 1000));

    if (isLogin) {
      // Login - Validar que el usuario exista
      const currentUsers = getUsersFromStorage();
      const user = currentUsers.find(u => u.email === email);

      if (!user) {
        setError('Este correo no est√° registrado. Por favor, reg√≠strate primero.');
        setIsLoading(false);
        return;
      }

      if (user.password !== hashPassword(password)) {
        setError('Contrase√±a incorrecta');
        setIsLoading(false);
        return;
      }

      setSuccess('¬°Inicio de sesi√≥n exitoso!');
      setTimeout(() => {
        onLogin(user);
      }, 500);

    } else {
      // Registro
      if (!fullName.trim()) {
        setError('El nombre completo es requerido');
        setIsLoading(false);
        return;
      }

      if (!validateEmail(email)) {
        setError('Por favor ingresa un email v√°lido');
        setIsLoading(false);
        return;
      }

      if (password.length < 6) {
        setError('La contrase√±a debe tener al menos 6 caracteres');
        setIsLoading(false);
        return;
      }

      const currentUsers = getUsersFromStorage();

      if (currentUsers.find(user => user.email === email)) {
        setError('Este correo ya est√° registrado. Puedes iniciar sesi√≥n.');
        setIsLoading(false);
        return;
      }

      const newUser = { 
        email, 
        password: hashPassword(password),
        fullName: fullName.trim()
      };
      const updatedUsers = [...currentUsers, newUser];

      setUsers(updatedUsers);
      saveUsersToStorage(updatedUsers);

      setSuccess('¬°Cuenta creada exitosamente! Redirigiendo...');

      setTimeout(() => {
        setIsLogin(true);
        setEmail('');
        setPassword('');
        setFullName('');
        setSuccess('');
        setError('');
        setIsLoading(false);
      }, 2000);
      return;
    }
    setIsLoading(false);
  };

  const clearForm = () => {
    setEmail('');
    setPassword('');
    setFullName('');
    setError('');
    setSuccess('');
    setShowPassword(false);
  };

  const switchMode = () => {
    setIsLogin(!isLogin);
    clearForm();
  };

  const passwordStrength = !isLogin ? getPasswordStrength(password) : null;

  return (
    <div className="auth-wrapper">
      <div className="auth-container-professional">
        {/* Header profesional */}
        <div className="auth-header-professional">
          <div className="company-logo">
            <div className="logo-icon">üìã</div>
            <div className="company-info">
              <h1>TaskMaster Pro</h1>
              <p>Gesti√≥n profesional de tareas</p>
            </div>
          </div>
        </div>

        {/* Contenido del formulario */}
        <div className="auth-content-professional">
          <div className="form-header">
            <h2>{isLogin ? 'Iniciar Sesi√≥n' : 'Crear Cuenta'}</h2>
            <p className="form-subtitle">
              {isLogin 
                ? 'Accede a tu espacio de trabajo' 
                : '√önete a TaskMaster Pro'
              }
            </p>
          </div>

          <form onSubmit={handleSubmit} className="auth-form-professional">
            {!isLogin && (
              <div className="form-group-professional">
                <label className="form-label">Nombre Completo</label>
                <div className="input-wrapper">
                  <span className="input-icon">üë§</span>
                  <input
                    type="text"
                    value={fullName}
                    onChange={(e) => setFullName(e.target.value)}
                    required={!isLogin}
                    placeholder="Ingresa tu nombre completo"
                    className="form-input-professional"
                    disabled={isLoading}
                  />
                </div>
              </div>
            )}

            <div className="form-group-professional">
              <label className="form-label">Correo Electr√≥nico</label>
              <div className="input-wrapper">
                <span className="input-icon">üìß</span>
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  placeholder="nombre@empresa.com"
                  className={`form-input-professional ${!isLogin && email && !validateEmail(email) ? 'input-error' : ''}`}
                  disabled={isLoading}
                />
                {!isLogin && email && (
                  <span className={`validation-icon ${validateEmail(email) ? 'valid' : 'invalid'}`}>
                    {validateEmail(email) ? '‚úì' : '‚úó'}
                  </span>
                )}
              </div>
            </div>

            <div className="form-group-professional">
              <label className="form-label">Contrase√±a</label>
              <div className="input-wrapper">
                <span className="input-icon">üîí</span>
                <input
                  type={showPassword ? 'text' : 'password'}
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  placeholder={isLogin ? "Tu contrase√±a" : "M√≠nimo 6 caracteres"}
                  className="form-input-professional"
                  disabled={isLoading}
                />
                <button
                  type="button"
                  className="password-toggle"
                  onClick={() => setShowPassword(!showPassword)}
                  disabled={isLoading}
                >
                  {showPassword ? 'üôà' : 'üëÅÔ∏è'}
                </button>
              </div>

              {/* Indicador de fuerza de contrase√±a */}
              {!isLogin && password && (
                <div className="password-strength">
                  <div className="strength-bar">
                    <div 
                      className="strength-fill" 
                      style={{ 
                        width: password.length < 6 ? '33%' : password.length < 8 ? '66%' : '100%',
                        backgroundColor: passwordStrength.color 
                      }}
                    ></div>
                  </div>
                  <span className="strength-text" style={{ color: passwordStrength.color }}>
                    {passwordStrength.text}
                  </span>
                </div>
              )}
            </div>

            {error && (
              <div className="alert alert-error">
                <span className="alert-icon">‚ö†Ô∏è</span>
                {error}
              </div>
            )}

            {success && (
              <div className="alert alert-success">
                <span className="alert-icon">‚úÖ</span>
                {success}
              </div>
            )}

            <button 
              type="submit" 
              className={`btn-professional ${isLoading ? 'loading' : ''}`}
              disabled={isLoading}
            >
              {isLoading ? (
                <span className="loading-content">
                  <span className="spinner"></span>
                  Procesando...
                </span>
              ) : (
                isLogin ? 'Iniciar Sesi√≥n' : 'Crear Cuenta'
              )}
            </button>
          </form>

          <div className="form-footer">
            <p>
              {isLogin ? '¬øNo tienes una cuenta?' : '¬øYa tienes una cuenta?'}
              <button 
                type="button"
                className="link-button" 
                onClick={switchMode}
                disabled={isLoading}
              >
                {isLogin ? 'Reg√≠strate aqu√≠' : 'Inicia sesi√≥n'}
              </button>
            </p>
          </div>

          {/* Credenciales de prueba */}
          <div className="demo-credentials">
            <h4>Cuenta de demostraci√≥n:</h4>
            <div className="demo-info">
              <div className="demo-item">
                <strong>Email:</strong> prueba@prueba.com
              </div>
              <div className="demo-item">
                <strong>Contrase√±a:</strong> 123456
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Componente de Categor√≠a
const CategorySection = ({ category, tasks, onAddTask, onToggleTask, onEditTask, onDeleteTask }) => {
  const [newTask, setNewTask] = useState('');
  const [isExpanded, setIsExpanded] = useState(true);

  const handleAddTask = (e) => {
    e.preventDefault();
    if (!newTask.trim()) return;

    onAddTask(newTask.trim(), category.id);
    setNewTask('');
  };

  const categoryTasks = tasks.filter(task => task.category === category.id);
  const completedCount = categoryTasks.filter(task => task.completed).length;

  return (
    <div className="category-section">
      <div 
        className="category-header" 
        onClick={() => setIsExpanded(!isExpanded)}
        style={{ borderLeftColor: category.color }}
      >
        <div className="category-info">
          <span className="category-icon">{category.icon}</span>
          <span className="category-name">{category.name}</span>
          <span className="task-count">
            {completedCount}/{categoryTasks.length}
          </span>
        </div>
        <span className={`expand-icon ${isExpanded ? 'expanded' : ''}`}>‚ñº</span>
      </div>

      {isExpanded && (
        <div className="category-content">
          <form onSubmit={handleAddTask} className="add-task-form">
            <div className="task-input-group">
              <input
                type="text"
                value={newTask}
                onChange={(e) => setNewTask(e.target.value)}
                placeholder={`Nueva tarea en ${category.name.toLowerCase()}...`}
                className="task-input"
              />
              <button type="submit" className="btn-add-task" style={{ backgroundColor: category.color }}>
                + Agregar
              </button>
            </div>
          </form>

          <div className="tasks-in-category">
            {categoryTasks.length === 0 ? (
              <div className="no-tasks-category">
                No hay tareas en {category.name.toLowerCase()}
              </div>
            ) : (
              categoryTasks.map(task => (
                <TaskItem
                  key={task.id}
                  task={task}
                  onToggle={onToggleTask}
                  onEdit={onEditTask}
                  onDelete={onDeleteTask}
                  categoryColor={category.color}
                />
              ))
            )}
          </div>
        </div>
      )}
    </div>
  );
};

// Componente de Tarea Individual mejorado
const TaskItem = ({ task, onToggle, onEdit, onDelete, categoryColor }) => {
  return (
    <div className={`task-item ${task.completed ? 'completed' : ''}`}>
      <div className="task-content">
        <div className="task-indicator" style={{ backgroundColor: categoryColor }}></div>
        <div className={`task-text ${task.completed ? 'completed' : ''}`}>
          {task.text}
        </div>
      </div>
      <div className="task-actions">
        <button 
          className="btn-small btn-complete" 
          onClick={() => onToggle(task.id)}
          title={task.completed ? 'Marcar como pendiente' : 'Marcar como completada'}
        >
          {task.completed ? '‚Ü∂' : '‚úì'}
        </button>
        <button 
          className="btn-small btn-edit" 
          onClick={() => onEdit(task.id)}
          title="Editar tarea"
        >
          ‚úé
        </button>
        <button 
          className="btn-small btn-delete" 
          onClick={() => onDelete(task.id)}
          title="Eliminar tarea"
        >
          ‚úó
        </button>
      </div>
