import React, { useState, useEffect } from 'react';
import './App.css';

// Funci√≥n para hashear contrase√±as (simulaci√≥n simple)
const hashPassword = (password) => {
  let hash = 0;
  for (let i = 0; i < password.length; i++) {
    const char = password.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash;
  }
  return hash.toString();
};

// Categor√≠as predefinidas
const CATEGORIES = [
  { id: 'personal', name: 'Personal', icon: 'üë§', color: '#667eea' },
  { id: 'supermercado', name: 'Supermercado', icon: 'üõí', color: '#4ecdc4' },
  { id: 'salud', name: 'Salud', icon: 'üè•', color: '#ff6b6b' },
  { id: 'citas', name: 'Citas', icon: 'üìÖ', color: '#feca57' },
  { id: 'reuniones', name: 'Reuniones', icon: 'üë•', color: '#48dbfb' },
  { id: 'llamadas', name: 'Llamadas', icon: 'üìû', color: '#ff9ff3' },
  { id: 'trabajo', name: 'Trabajo', icon: 'üíº', color: '#54a0ff' },
  { id: 'estudios', name: 'Estudios', icon: 'üìö', color: '#5f27cd' },
  { id: 'hogar', name: 'Hogar', icon: 'üè†', color: '#00d2d3' },
  { id: 'otros', name: 'Otros', icon: 'üìù', color: '#6c757d' }
];

// Componente de Login/Registro con dise√±o profesional
const AuthForm = ({ onLogin }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [fullName, setFullName] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Funci√≥n para obtener usuarios del localStorage
  const getUsersFromStorage = () => {
    try {
      const storedUsers = localStorage.getItem('todoapp_users');
      return storedUsers ? JSON.parse(storedUsers) : [];
    } catch (error) {
      console.error('Error al leer usuarios del localStorage:', error);
      return [];
    }
  };

  // Funci√≥n para guardar usuarios en localStorage
  const saveUsersToStorage = (users) => {
    try {
      localStorage.setItem('todoapp_users', JSON.stringify(users));
    } catch (error) {
      console.error('Error al guardar usuarios en localStorage:', error);
    }
  };

  // Inicializar usuarios desde localStorage
  const [users, setUsers] = useState(() => {
    const storedUsers = getUsersFromStorage();
    // Si no hay usuarios, crear uno de prueba
    if (storedUsers.length === 0) {
      const defaultUsers = [
        { 
          email: 'prueba@prueba.com', 
          password: hashPassword('123456'),
          fullName: 'Usuario de Prueba'
        }
      ];
      saveUsersToStorage(defaultUsers);
      return defaultUsers;
    }
    return storedUsers;
  });

  // Actualizar localStorage cuando cambie el estado de usuarios
  useEffect(() => {
    saveUsersToStorage(users);
  }, [users]);

  // Validaciones en tiempo real
  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const getPasswordStrength = (password) => {
    if (password.length < 6) return { strength: 'weak', text: 'Muy d√©bil', color: '#ff4757' };
    if (password.length < 8) return { strength: 'medium', text: 'Media', color: '#ffa502' };
    if (password.length >= 8 && /(?=.*[a-z])(?=.*[A-Z])(?=.*\d)/.test(password)) {
      return { strength: 'strong', text: 'Fuerte', color: '#2ed573' };
    }
    return { strength: 'medium', text: 'Media', color: '#ffa502' };
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setIsLoading(true);

    // Simular delay de red
    await new Promise(resolve => setTimeout(resolve, 1000));

    if (isLogin) {
      // Login - Validar que el usuario exista
      const currentUsers = getUsersFromStorage();
      const user = currentUsers.find(u => u.email === email);

      if (!user) {
        setError('Este correo no est√° registrado. Por favor, reg√≠strate primero.');
        setIsLoading(false);
        return;
      }

      if (user.password !== hashPassword(password)) {
        setError('Contrase√±a incorrecta');
        setIsLoading(false);
        return;
      }

      setSuccess('¬°Inicio de sesi√≥n exitoso!');
      setTimeout(() => {
        onLogin(user);
      }, 500);

    } else {
      // Registro
      if (!fullName.trim()) {
        setError('El nombre completo es requerido');
        setIsLoading(false);
        return;
      }

      if (!validateEmail(email)) {
        setError('Por favor ingresa un email v√°lido');
        setIsLoading(false);
        return;
      }

      if (password.length < 6) {
        setError('La contrase√±a debe tener al menos 6 caracteres');
        setIsLoading(false);
        return;
      }

      const currentUsers = getUsersFromStorage();

      if (currentUsers.find(user => user.email === email)) {
        setError('Este correo ya est√° registrado. Puedes iniciar sesi√≥n.');
        setIsLoading(false);
        return;
      }

      const newUser = { 
        email, 
        password: hashPassword(password),
        fullName: fullName.trim()
      };
      const updatedUsers = [...currentUsers, newUser];

      setUsers(updatedUsers);
      saveUsersToStorage(updatedUsers);

      setSuccess('¬°Cuenta creada exitosamente! Redirigiendo...');

      setTimeout(() => {
        setIsLogin(true);
        setEmail('');
        setPassword('');
        setFullName('');
        setSuccess('');
        setError('');
        setIsLoading(false);
      }, 2000);
      return;
    }
    setIsLoading(false);
  };

  const clearForm = () => {
    setEmail('');
    setPassword('');
    setFullName('');
    setError('');
    setSuccess('');
    setShowPassword(false);
  };

  const switchMode = () => {
    setIsLogin(!isLogin);
    clearForm();
  };

  const passwordStrength = !isLogin ? getPasswordStrength(password) : null;

  return (
    <div className="auth-wrapper">
      <div className="auth-container-professional">
        {/* Header profesional */}
        <div className="auth-header-professional">
          <div className="company-logo">
            <div className="logo-icon">üìã</div>
            <div className="company-info">
              <h1>TaskMaster Pro</h1>
              <p>Gesti√≥n profesional de tareas</p>
            </div>
          </div>
        </div>

        {/* Contenido del formulario */}
        <div className="auth-content-professional">
          <div className="form-header">
            <h2>{isLogin ? 'Iniciar Sesi√≥n' : 'Crear Cuenta'}</h2>
            <p className="form-subtitle">
              {isLogin 
                ? 'Accede a tu espacio de trabajo' 
                : '√önete a TaskMaster Pro'
              }
            </p>
          </div>

          <form onSubmit={handleSubmit} className="auth-form-professional">
            {!isLogin && (
              <div className="form-group-professional">
                <div className="form-label-wrapper">
                  <span className="form-label-icon">üë§</span>
                  <label className="form-label">Nombre Completo</label>
                </div>
                <div className="input-wrapper">
                  <input
                    type="text"
                    value={fullName}
                    onChange={(e) => setFullName(e.target.value)}
                    required={!isLogin}
                    placeholder="Ingresa tu nombre completo"
                    className="form-input-professional"
                    disabled={isLoading}
                  />
                </div>
              </div>
            )}

            <div className="form-group-professional">
              <div className="form-label-wrapper">
                <span className="form-label-icon">üìß</span>
                <label className="form-label">Correo Electr√≥nico</label>
              </div>
              <div className="input-wrapper">
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  placeholder="nombre@empresa.com"
                  className={`form-input-professional ${!isLogin && email && !validateEmail(email) ? 'input-error' : ''}`}
                  disabled={isLoading}
                />
                {!isLogin && email && (
                  <span className={`validation-icon ${validateEmail(email) ? 'valid' : 'invalid'}`}>
                    {validateEmail(email) ? '‚úì' : '‚úó'}
                  </span>
                )}
              </div>
            </div>

            <div className="form-group-professional">
              <div className="form-label-wrapper">
                <span className="form-label-icon">üîí</span>
                <label className="form-label">Contrase√±a</label>
              </div>
              <div className="input-wrapper">
                <input
                  type={showPassword ? 'text' : 'password'}
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  placeholder={isLogin ? "Tu contrase√±a" : "M√≠nimo 6 caracteres"}
                  className="form-input-professional"
                  disabled={isLoading}
                />
                <button
                  type="button"
                  className="password-toggle"
                  onClick={() => setShowPassword(!showPassword)}
                  disabled={isLoading}
                >
                  {showPassword ? 'üôà' : 'üëÅÔ∏è'}
                </button>
              </div>

              {/* Indicador de fuerza de contrase√±a */}
              {!isLogin && password && (
                <div className="password-strength">
                  <div className="strength-bar">
                    <div 
                      className="strength-fill" 
                      style={{ 
                        width: password.length < 6 ? '33%' : password.length < 8 ? '66%' : '100%',
                        backgroundColor: passwordStrength.color 
                      }}
                    ></div>
                  </div>
                  <span className="strength-text" style={{ color: passwordStrength.color }}>
                    {passwordStrength.text}
                  </span>
                </div>
              )}
            </div>

            {error && (
              <div className="alert alert-error">
                <span className="alert-icon">‚ö†Ô∏è</span>
                {error}
              </div>
            )}

            {success && (
              <div className="alert alert-success">
                <span className="alert-icon">‚úÖ</span>
                {success}
              </div>
            )}

            <button 
              type="submit" 
              className={`btn-professional ${isLoading ? 'loading' : ''}`}
              disabled={isLoading}
            >
              {isLoading ? (
                <span className="loading-content">
                  <span className="spinner"></span>
                  Procesando...
                </span>
              ) : (
                isLogin ? 'Iniciar Sesi√≥n' : 'Crear Cuenta'
              )}
            </button>
          </form>

          <div className="form-footer">
            <p>
              {isLogin ? '¬øNo tienes una cuenta?' : '¬øYa tienes una cuenta?'}
              <button 
                type="button"
                className="link-button" 
                onClick={switchMode}
                disabled={isLoading}
              >
                {isLogin ? 'Reg√≠strate aqu√≠' : 'Inicia sesi√≥n'}
              </button>
            </p>
          </div>

          {/* Credenciales de prueba */}
          <div className="demo-credentials">
            <h4>Cuenta de demostraci√≥n:</h4>
            <div className="demo-info">
              <div className="demo-item">
                <strong>Email:</strong> prueba@prueba.com
              </div>
              <div className="demo-item">
                <strong>Contrase√±a:</strong> 123456
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Componente de Alertas de Tareas Vencidas
const TaskAlerts = ({ tasks }) => {
  const today = new Date();
  const todayString = today.toISOString().split('T')[0];

  // Tareas vencidas
  const overdueTasks = tasks.filter(task => 
    !task.completed && 
    task.deadline && 
    new Date(task.deadline) < today
  );

  // Tareas de hoy
  const todayTasks = tasks.filter(task => 
    !task.completed && 
    task.deadline === todayString
  );

  // Tareas de ma√±ana
  const tomorrow = new Date(today);
  tomorrow.setDate(tomorrow.getDate() + 1);
  const tomorrowString = tomorrow.toISOString().split('T')[0];

  const tomorrowTasks = tasks.filter(task => 
    !task.completed && 
    task.deadline === tomorrowString
  );

  if (overdueTasks.length === 0 && todayTasks.length === 0 && tomorrowTasks.length === 0) {
    return null;
  }

  return (
    <div className="task-alerts">
      {overdueTasks.length > 0 && (
        <div className="alert-section alert-overdue">
          <div className="alert-header">
            <span className="alert-icon">‚ö†Ô∏è</span>
            <span className="alert-title">Tareas Vencidas ({overdueTasks.length})</span>
          </div>
          <div className="alert-tasks">
            {overdueTasks.slice(0, 3).map(task => (
              <div key={task.id} className="alert-task">
                <span className="alert-task-text">{task.text}</span>
                <span className="alert-task-date">
                  {new Date(task.deadline).toLocaleDateString('es-ES')}
                </span>
              </div>
            ))}
            {overdueTasks.length > 3 && (
              <div className="alert-more">
                +{overdueTasks.length - 3} m√°s
              </div>
            )}
          </div>
        </div>
      )}

      {todayTasks.length > 0 && (
        <div className="alert-section alert-today">
          <div className="alert-header">
            <span className="alert-icon">üìÖ</span>
            <span className="alert-title">Para Hoy ({todayTasks.length})</span>
          </div>
          <div className="alert-tasks">
            {todayTasks.slice(0, 3).map(task => (
              <div key={task.id} className="alert-task">
                <span className="alert-task-text">{task.text}</span>
              </div>
            ))}
            {todayTasks.length > 3 && (
              <div className="alert-more">
                +{todayTasks.length - 3} m√°s
              </div>
            )}
          </div>
        </div>
      )}

      {tomorrowTasks.length > 0 && (
        <div className="alert-section alert-tomorrow">
          <div className="alert-header">
            <span className="alert-icon">‚è∞</span>
            <span className="alert-title">Para Ma√±ana ({tomorrowTasks.length})</span>
          </div>
          <div className="alert-tasks">
            {tomorrowTasks.slice(0, 2).map(task => (
              <div key={task.id} className="alert-task">
                <span className="alert-task-text">{task.text}</span>
              </div>
            ))}
            {tomorrowTasks.length > 2 && (
              <div className="alert-more">
                +{tomorrowTasks.length - 2} m√°s
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

// Componente de Tarea Individual con fechas l√≠mite y alertas
const TaskItemExtended = ({ task, onToggle, onEdit, onDelete, categoryColor }) => {
  // Funci√≥n para obtener el estado de la fecha l√≠mite
  const getDeadlineStatus = (deadline) => {
    if (!deadline) return null;

    const today = new Date();
    const deadlineDate = new Date(deadline);
    const diffTime = deadlineDate - today;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays < 0) {
      return { status: 'overdue', text: 'Vencida', color: '#ff4757', days: Math.abs(diffDays) };
    } else if (diffDays === 0) {
      return { status: 'today', text: 'Hoy', color: '#ff6b35', days: 0 };
    } else if (diffDays === 1) {
      return { status: 'tomorrow', text: 'Ma√±ana', color: '#f39c12', days: 1 };
    } else if (diffDays <= 3) {
      return { status: 'soon', text: `${diffDays} d√≠as`, color: '#f39c12', days: diffDays };
    } else if (diffDays <= 7) {
      return { status: 'week', text: `${diffDays} d√≠as`, color: '#3498db', days: diffDays };
    } else {
      return { status: 'future', text: `${diffDays} d√≠as`, color: '#95a5a6', days: diffDays };
    }
  };

  // Funci√≥n para obtener el color de la prioridad
  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'high': return '#ff4757';
      case 'medium': return '#ffa502';
      case 'low': return '#2ed573';
      default: return '#95a5a6';
    }
  };

  // Funci√≥n para obtener el icono de prioridad
  const getPriorityIcon = (priority) => {
    switch (priority) {
      case 'high': return 'üî¥';
      case 'medium': return 'üü°';
      case 'low': return 'üü¢';
      default: return '‚ö™';
    }
  };

  const deadlineStatus = getDeadlineStatus(task.deadline);
  const priorityColor = getPriorityColor(task.priority);
  const priorityIcon = getPriorityIcon(task.priority);

  // Formatear fecha para mostrar
  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleDateString('es-ES', { 
      day: '2-digit', 
      month: '2-digit', 
      year: 'numeric' 
    });
  };

  return (
    <div className={`task-item-extended ${task.completed ? 'completed' : ''} ${deadlineStatus?.status || ''}`}>
      <div className="task-main-content">
        <div className="task-indicators">
          <div className="task-category-indicator" style={{ backgroundColor: categoryColor }}></div>
          {task.priority && (
            <div className="task-priority-indicator" style={{ color: priorityColor }}>
              {priorityIcon}
            </div>
          )}
        </div>

        <div className="task-text-content">
          <div className={`task-text ${task.completed ? 'completed' : ''}`}>
            {task.text}
          </div>

          {task.deadline && (
            <div className="task-deadline" style={{ color: deadlineStatus.color }}>
              <span className="deadline-icon">üìÖ</span>
              <span className="deadline-text">
                {formatDate(task.deadline)} ‚Ä¢ {deadlineStatus.text}
                {deadlineStatus.status === 'overdue' && ` (${deadlineStatus.days} d√≠as)`}
              </span>
            </div>
          )}
        </div>
      </div>

      <div className="task-actions">
        <button 
          className="btn-small btn-complete" 
          onClick={() => onToggle(task.id)}
          title={task.completed ? 'Marcar como pendiente' : 'Marcar como completada'}
        >
          {task.completed ? '‚Ü∂' : '‚úì'}
        </button>
        <button 
          className="btn-small btn-edit" 
          onClick={() => onEdit(task.id)}
          title="Editar tarea"
        >
          ‚úé
        </button>
        <button 
          className="btn-small btn-delete" 
          onClick={() => onDelete(task.id)}
          title="Eliminar tarea"
        >
          ‚úó
        </button>
      </div>
    </div>
  );
};

// Componente de Categor√≠a con fechas l√≠mite
const CategorySection = ({ category, tasks, onAddTask, onToggleTask, onEditTask, onDeleteTask }) => {
  const [newTask, setNewTask] = useState('');
  const [newTaskDeadline, setNewTaskDeadline] = useState('');
  const [newTaskPriority, setNewTaskPriority] = useState('medium');
  const [isExpanded, setIsExpanded] = useState(true);

  const handleAddTask = (e) => {
    e.preventDefault();
    if (!newTask.trim()) return;

    const taskData = {
      text: newTask.trim(),
      deadline: newTaskDeadline || null,
      priority: newTaskPriority
    };

    onAddTask(taskData, category.id);
    setNewTask('');
    setNewTaskDeadline('');
    setNewTaskPriority('medium');
  };

  const categoryTasks = tasks.filter(task => task.category === category.id);
  const completedCount = categoryTasks.filter(task => task.completed).length;

  // Funci√≥n para obtener la fecha m√≠nima (hoy)
  const getTodayDate = () => {
    const today = new Date();
    return today.toISOString().split('T')[0];
  };

  // Ordenar tareas por prioridad y fecha l√≠mite
  const sortedTasks = categoryTasks.sort((a, b) => {
    // Primero por completado (no completadas primero)
    if (a.completed !== b.completed) {
      return a.completed ? 1 : -1;
    }

    // Luego por prioridad
    const priorityOrder = { high: 3, medium: 2, low: 1 };
    const priorityDiff = (priorityOrder[b.priority] || 2) - (priorityOrder[a.priority] || 2);
    if (priorityDiff !== 0) return priorityDiff;

    // Finalmente por fecha l√≠mite (m√°s pr√≥ximas primero)
    if (a.deadline && b.deadline) {
      return new Date(a.deadline) - new Date(b.deadline);
    }
    if (a.deadline) return -1;
    if (b.deadline) return 1;
    return 0;
  });

  return (
    <div className="category-section">
      <div 
        className="category-header" 
        onClick={() => setIsExpanded(!isExpanded)}
        style={{ borderLeftColor: category.color }}
      >
        <div className="category-info">
          <span className="category-icon">{category.icon}</span>
          <span className="category-name">{category.name}</span>
          <span className="task-count">
            {completedCount}/{categoryTasks.length}
          </span>
        </div>
        <span className={`expand-icon ${isExpanded ? 'expanded' : ''}`}>‚ñº</span>
      </div>

      {isExpanded && (
        <div className="category-content">
          <form onSubmit={handleAddTask} className="add-task-form">
            <div className="task-input-group-extended">
              <input
                type="text"
                value={newTask}
                onChange={(e) => setNewTask(e.target.value)}
                placeholder={`Nueva tarea en ${category.name.toLowerCase()}...`}
                className="task-input-main"
                required
              />

              <div className="task-options">
                <div className="task-option">
                  <label className="task-option-label">üìÖ Fecha l√≠mite:</label>
                  <input
                    type="date"
                    value={newTaskDeadline}
                    onChange={(e) => setNewTaskDeadline(e.target.value)}
                    className="task-date-input"
                    min={getTodayDate()}
                  />
                </div>

                <div className="task-option">
                  <label className="task-option-label">‚ö° Prioridad:</label>
                  <select
                    value={newTaskPriority}
                    onChange={(e) => setNewTaskPriority(e.target.value)}
                    className="task-priority-select"
                  >
                    <option value="low">üü¢ Baja</option>
                    <option value="medium">üü° Media</option>
                    <option value="high">üî¥ Alta</option>
                  </select>
                </div>
              </div>

              <button type="submit" className="btn-add-task-extended" style={{ backgroundColor: category.color }}>
                ‚ûï Agregar Tarea
              </button>
            </div>
          </form>

          <div className="tasks-in-category">
            {categoryTasks.length === 0 ? (
              <div className="no-tasks-category">
                No hay tareas en {category.name.toLowerCase()}
              </div>
            ) : (
              sortedTasks.map(task => (
                <TaskItemExtended
                  key={task.id}
                  task={task}
                  onToggle={onToggleTask}
                  onEdit={onEditTask}
                  onDelete={onDeleteTask}
                  categoryColor={category.color}
                />
              ))
            )}
          </div>
        </div>
      )}
    </div>
  );
};

// Componente Principal de Tareas con fechas l√≠mite
const TodoApp = ({ user, onLogout }) => {
  const [tasks, setTasks] = useState([]);
  const [taskIdCounter, setTaskIdCounter] = useState(1);

  // Cargar tareas del localStorage al inicializar
  useEffect(() => {
    try {
      const storedTasks = localStorage.getItem(`todoapp_tasks_${user.email}`);
      if (storedTasks) {
        const parsedTasks = JSON.parse(storedTasks);
        setTasks(parsedTasks);
        // Establecer el contador basado en el ID m√°s alto
        if (parsedTasks.length > 0) {
          const maxId = Math.max(...parsedTasks.map(task => task.id));
          setTaskIdCounter(maxId + 1);
        }
      }
    } catch (error) {
      console.error('Error al cargar tareas del localStorage:', error);
    }
  }, [user.email]);

  // Guardar tareas en localStorage cuando cambien
  useEffect(() => {
    try {
      localStorage.setItem(`todoapp_tasks_${user.email}`, JSON.stringify(tasks));
    } catch (error) {
      console.error('Error al guardar tareas en localStorage:', error);
    }
  }, [tasks, user.email]);

  const addTask = (taskData, categoryId) => {
    const task = {
      id: taskIdCounter,
      text: taskData.text || taskData, // Compatibilidad con versi√≥n anterior
      completed: false,
      category: categoryId,
      userId: user.email,
      createdAt: new Date().toISOString(),
      deadline: taskData.deadline || null,
      priority: taskData.priority || 'medium'
    };

    setTasks([...tasks, task]);
    setTaskIdCounter(taskIdCounter + 1);
  };

  const toggleTask = (taskId) => {
    setTasks(tasks.map(task => 
      task.id === taskId 
        ? { ...task, completed: !task.completed }
        : task
    ));
  };

  const editTask = (taskId) => {
    const task = tasks.find(t => t.id === taskId);
    if (task) {
      const newText = prompt('Editar tarea:', task.text);
      if (newText && newText.trim()) {
        setTasks(tasks.map(t => 
          t.id === taskId 
            ? { ...t, text: newText.trim() }
            : t
        ));
      }
    }
  };

  const deleteTask = (taskId) => {
    if (window.confirm('¬øEst√°s seguro de que quieres eliminar esta tarea?')) {
      setTasks(tasks.filter(task => task.id !== taskId));
    }
  };

  const userTasks = tasks.filter(task => task.userId === user.email);
  const totalTasks = userTasks.length;
  const completedTasks = userTasks.filter(task => task.completed).length;

  // Estad√≠sticas adicionales
  const pendingTasks = userTasks.filter(task => !task.completed);
  const overdueTasks = pendingTasks.filter(task => 
    task.deadline && new Date(task.deadline) < new Date()
  ).length;
  const todayTasks = pendingTasks.filter(task => {
    if (!task.deadline) return false;
    const today = new Date().toISOString().split('T')[0];
    return task.deadline === today;
  }).length;

  return (
    <div className="todo-container">
      <div className="user-info">
        <div className="user-welcome">
          <span className="welcome-text">¬°Hola, {user.fullName}! üëã</span>
          <span className="user-email">{user.email}</span>
          <div className="progress-info">
            <span>{completedTasks}/{totalTasks} completadas</span>
            {overdueTasks > 0 && (
              <span className="overdue-count"> ‚Ä¢ {overdueTasks} vencidas</span>
            )}
            {todayTasks > 0 && (
              <span className="today-count"> ‚Ä¢ {todayTasks} para hoy</span>
            )}
          </div>
        </div>
        <button onClick={onLogout} className="btn btn-secondary logout-btn">
          Cerrar Sesi√≥n
        </button>
      </div>

      {/* Alertas de tareas importantes */}
      <TaskAlerts tasks={userTasks} />

      <div className="categories-container">
        {CATEGORIES.map(category => (
          <CategorySection
            key={category.id}
            category={category}
            tasks={userTasks}
            onAddTask={addTask}
            onToggleTask={toggleTask}
            onEditTask={editTask}
            onDeleteTask={deleteTask}
          />
        ))}
      </div>
    </div>
  );
};

// Componente Principal de la App
function App() {
  const [currentUser, setCurrentUser] = useState(null);

  const handleLogin = (user) => {
    setCurrentUser(user);
  };

  const handleLogout = () => {
    setCurrentUser(null);
  };

  return (
    <div className="App">
      {currentUser ? (
        <TodoApp user={currentUser} onLogout={handleLogout} />
      ) : (
        <AuthForm onLogin={handleLogin} />
      )}
    </div>
  );
}

export default App;
